<?phpinclude_once("../public/sql_connect.php");include_once("../public/my_msg.php");?><?phpif($_POST['goods_name'] == '' or $_POST['goods_type'] == '' or $_POST['goods_price'] == '' or $_POST['goods_num'] == ''){	my_msg("不能为空白","goods_release.php");}if($_POST['goods_judge'] == '' or $_POST['goods_des'] == '' or $_POST['goods_flag'] == '' or $_POST['telephone'] == ''){	my_msg("不能为空白","goods_release.php");}if(isset($_COOKIE['cookie_id'])){$sqlstr="insert into goods values ('','".$_COOKIE['cookie_id']."','".$_POST['goods_name']."','".$_POST['goods_type']."',		'".$_POST['goods_price']."','".$_POST['goods_num']."','".$_POST['goods_judge']."','".$_POST['goods_flag']."','".$_POST['goods_des']."',		'".$_POST['telephone']."','')";}$result = mysql_query($sqlstr,$link);if($_REQUEST["update"]){	if($_FILES["myfile"]["error"] == UPLOAD_ERR_OK)	{		if(!is_dir("../goods_picture"))		{			mkdir("../goods_picture");		}		$picture_name = $_FILES["myfile"]["name"];		$username = $_COOKIE['cookie_id'];		$newfilename = "../goods_picture/".$_FILES["myfile"]["name"]; 		if(move_uploaded_file($_FILES["myfile"]["tmp_name"],iconv("UTF-8","gb2312",$newfilename)))		{			$sqlstr="update goods set goods_picture = '".$picture_name."' where user_name = '".$username."' 			and goods_name = '".$_POST['goods_name']."'";			$result = mysql_query($sqlstr,$link);			my_msg("提交成功，回到会员功能专区","main.php");		}		else		{			echo "无法移动文件！";		}	}	else	{		echo "上传失败： ".$_FILES['myfile']['error']."<br />";	}}mysql_free_result($result);mysql_close($link);?>