<?phpinclude_once("../public/sql_connect.php");include_once("../public/my_msg.php");?><?phpif($_POST['user_name'] == '' or $_POST['user_password'] == ''){my_msg("输入不能为空白","login.php");}$sqlstr="select * from regist where user_name='".$_POST['user_name']."' and user_password='".$_POST['user_password']."'";$result=mysql_query($sqlstr,$link);$row = mysql_fetch_array($result,MYSQL_BOTH);if(mysql_num_rows($result) == 1 && $row['user_rank'] == 2){setcookie("cookie_chk","adm_logined");setcookie("cookie_id",$_POST['user_name']);my_header("adm.php");//管理员登录}if(mysql_num_rows($result) == 1 && $row['user_rank'] == 1){setcookie("cookie_chk","logined");setcookie("cookie_id",$_POST['user_name']);my_header("main.php");//会员登录}else{my_msg("登录失败，请重新登录","login.php");}mysql_free_result($result);mysql_close($link);?>